INSERT INTO `module_menus` VALUES (1,'凝聚','Cohesion','1','形成团队合力，增加归属感，激活个体动力','images/ac3b62834c85078b26a38fd72277d6d7.png',NULL,'#FF5000','images/0a42af2629d909c4cc617fca7fcf6fca.png','[\"images/462a4c9e995f26f604956e2ca5836228.png\", \"images/be40f0d2d97dfa59a239d8197e56bc12.png\"]','[\"对目标不关心\", \"认同理解不够\", \"合力不足\", \"投入低缺热情\"]','凝聚类痛点&解法概览','videos/78b30a0ccf0e3f32be0384f97d048168.mp4','images/67e2a17dbfeb86514759f3b38706f917.png','凝聚4类痛点、7类解决方向、5个误区、9个解决路径','03:36',3,'2021-12-10 07:14:31','2022-01-26 07:46:22'),(2,'协作','Collaboration','1','提升团队协同效率','images/c788fe5bba883c13c26dd6893a049036.png','images/0b6c61945b5535e1d0f58a07f125d0b3.png','#4A65F7','images/f27f76b60c6edfdcc10971716c6fd945.png','[\"images/df98893491c2e1e61714c7e5c8bd980a.png\", \"images/22f467f979b4c713b6b09df4cd004a4c.png\"]','[\"前中后台矛盾多\", \"推诿\", \"协同效率低\", \"资源发挥不足\", \"部门墙\"]','协同类痛点&解法概览','videos/39e1441e6a5e281b52d51429699dc392.mp4','images/4664303b384fcea70f08deee58ee30bc.png','协作相关3类痛点、9类解决方向、10个解决路径','04:37',1,'2021-12-10 07:44:27','2022-01-26 07:46:20'),(3,'对齐','Alignment','1','对齐理解，力出一孔','images/4bd6b7b83730ffa7992da2112b8efbe5.png','images/d426938a4ba89aa8aa62efca0a372841.png','#FF9E23','images/11306c828339842fca24e251907baddf.png','[\"images/bbe4380de8a68c7d2f7c30a897d8ad15.png\", \"images/4455d605767dacc90d742686eb8e5a1b.png\"]','[\"业务理解对不齐\", \"对外部反应迟钝\", \"方向说多遍变形\", \"内部视角分歧大\"]','对齐类痛点&解法概览','videos/6786028c1c747596f856afed89e2fadd.mp4','images/7df323f9d75429164b38578bfb67f574.png','对齐4类痛点、5类对齐方向、7个路径、对齐重要心智','03:57',2,'2021-12-21 12:31:35','2022-01-26 07:46:22'),(4,'策略','Strategy','1','实现目标的关键行动/路径打法','images/2cde27a060fdd5e7dcdc8bb12766b053.png','images/7e229ad9551f54198caa746d7fba37e8.png','#64B553','images/68f8a0e2c1d1f76d479f34c841e9233c.png','[\"images/e4b7ad08b9414ed51062b5ad035ff2a4.png\", \"images/283584073194ff9fa25cfb6c4d503afd.png\"]','[\"总是常规做法没突破\", \"打法细化没章法\", \"贡献想法不积极\", \"策略执行推进慢\"]','策略类痛点&解法概览','videos/6c2ba7d475dc716638cc6a370097722c.mp4','images/d6a61d2343d4bd0506a90f50eac7e847.png','策略相关6类痛点、8类解决方向、5个解决路径','03:36',4,'2021-12-21 12:37:55','2022-02-11 03:10:59'),(5,'成长','Growth','1','成长跟得上业务','images/cb8f21cfb0c437b364d7c6ce29ff902c.png','images/b005b52f9f9be2489cf6df9998ba9b79.png','#4C5EFF','images/b5cd38f92d07495e34c002e15f954238.png','[\"images/62871a9c7ce50cbc2a957feb38704cd8.png\", \"images/197950eb5de645a736aba3c066fc2d5e.png\"]','[\"培训看不到效果\", \"管理层成长跟不上业务\", \"心态和思维格局不够\", \"业务复制慢\"]','成长类痛点&解法','videos/ab6511d0f13ccad256804afb0f6eabfc.mp4','images/0d54919b6676db9c84f503502a0a9540.png','成长相关3类痛点、3种解决方向，9个解决路径','03:33',5,'2021-12-21 12:41:48','2022-02-11 03:10:59'),(6,'氛围','Atmosphere','2','团队所处的环境氛围','images/b7af4ef237234bae4a7aed3a64b3009b.png','images/81e078710d2312c6bf21bb56a45293f3.png','#FF9E23','images/fd65f3dc4d2086fd0dbc82dd52627eb2.png','[\"images/9acaf3c490709074f7fc9e0c4d6bff1d.png\", \"images/c4310bb816fe7ee8d45f08fccbbba522.png\"]','[\"文化逐渐稀释\", \"思维不到位&行为越界\", \"价值观难落地\", \"宣讲传达效果不佳\"]','氛围类痛点&解法概览','videos/dbe390ba2deb04c2c18aa6a56ecaa869.mp4','images/476db95f45685e6552043cdfb07f0113.png','氛围3类痛点、对人3层影响、4类解决方向、5个解决路径','02:37',6,'2021-12-21 12:50:09','2022-02-11 03:11:03'),(7,'信任','Trust','2','信任提高沟通效率和决策品质','images/c8879cacb9dd7bb0bd0c6dd8dd7e8b3d.png','images/65a8a2e645b630348a2d9fc3282558b9.png','#FF5000','images/b473e4c2ce6a7b7536aad1ac18844093.png','[\"images/a0301dd8023fc1270eb2855fd47bfd0e.png\", \"images/7697dee8be1c2cb5da475ebb8b5f0304.png\"]','[\"不直言想法\", \"沟通氛围差\", \"有隔阂戒备\", \"归属感&关系不够\"]','信任类痛点&解法概览','videos/8b06378592a6e9a7987e2f3c5658a3dd.mp4','images/f18093bd4daeaa7757f66c5f315cb2e7.png','信任4类痛点、对业务影响、3类解决方向、10个解决路径','03:28',7,'2021-12-21 12:54:03','2022-02-11 03:11:06'),(8,'士气','Morale','2','提升团队能量、信心、效能感','images/40a5d4642e7ae3fed63dd8da12cd1dce.png','images/ef29b47805f81c6ebecc13973fe51cf4.png','#5B4AF6','images/70f5b07b4c5375de731a2a7c697ee080.png','[\"images/85409578ce5b114f59b7e9591533527e.png\", \"images/ed0179c431ebe373d45f210bce998f08.png\"]','[\"状态低落\", \"无力应对外部变化\", \"信心不足焦虑\", \"挑战低谷难跨越\"]','士气类痛点&解法概览','videos/e4b937135ef9c7b94d39624e613ef0b3.mp4','images/9cddf8a3eb0cb9124e0bbb357cd172ca.png','士气相关3类痛点、4类解决方向、7个解决路径','01:51',8,'2021-12-21 12:57:36','2022-02-11 03:11:07'),(9,'效率','Efficiency','2','团队有效性和高效性','images/a6ddea42d87324e05e9f082753c1b0b1.png','images/cea123f74541fc1f69c2a460c3555c25.png','#64B553','images/2e759a03f4a1b142152fafb0acc72411.png','[\"images/6f1916d456104975e601db100772f6d6.png\", \"images/3fd762db273eab727720ebd730f7c16a.png\"]','[\"执行推动慢\", \"内部运营效率低\", \"人效不理想\"]','效率类痛点&解法概览','videos/iyejsvjd6elnjyztpo2362a3kyskm2yv.mp4','images/2376ccb8e852a7c42e2389483a710e9c.png',NULL,'08:43',9,'2021-12-21 13:00:44','2022-02-11 03:11:10'),(10,'原理','high-performance team','2','「高效团队」认知地图，收获团队打造系统思考','images/aa91576948470125f507ad630ea1ab8b.png',NULL,'#FF9E23','images/bdaa155c02c79ed52ddec8c2a5632be6.png','[\"images/822161d96934ef95c387d3d77edf4413.png\", \"images/096011990b558908b50d64c21bf2818d.png\"]','[\"高效团队地图“七要素”\", \"高效团队“评估指标”\", \"团队内核-管理者心智\"]','「高效团队」认知地图',NULL,'images/073f3252347b94367ade4cc58529a083.jpg',NULL,NULL,10,'2021-12-21 13:02:25','2022-02-11 03:11:10'),(11,'目标','Target',NULL,'目标共振、目标拆解',NULL,NULL,NULL,NULL,'[]','[\"拆解目标讨价还价\", \"目标重要性难理解\"]',NULL,NULL,NULL,NULL,NULL,13,'2022-01-26 07:49:56','2022-01-27 03:42:26'),(12,'策略拆解/突破','strategy',NULL,'策略拆解',NULL,NULL,NULL,NULL,'[]','[\"想法不主动\", \"拆解目标没章法\", \"客户敏感度不够\"]',NULL,NULL,NULL,NULL,NULL,11,'2022-01-26 07:51:27','2022-01-28 00:51:34'),(13,'过程管理','Communication',NULL,'绩效面谈、执行过程辅导',NULL,NULL,NULL,NULL,'[]','[\"绩效面谈怎么激发员工\", \"评判表扬怎样有效\"]',NULL,NULL,NULL,NULL,NULL,12,'2022-01-26 07:52:45','2022-01-27 03:42:26'),(14,'复盘迭代','improve',NULL,NULL,NULL,NULL,NULL,NULL,'[]','[\"前线槽点怎么转化成行动\", \"什么复盘有效\", \"如何有效沉淀经验\"]',NULL,NULL,NULL,NULL,NULL,14,'2022-01-26 07:54:50','2022-01-26 07:54:50');
